package com.heshan.framework.exception;import java.io.Serializable;public abstract class BaseException extends RuntimeException {    private static final long serialVersionUID = -3848547174252753762L;    protected boolean isAjax = false;    public BaseException() {    }    public BaseException(int code, String msg) {        super(msg);    }    public BaseException(String message) {        super(message);    }    public BaseException(Throwable cause) {        super(cause);    }    public BaseException(String message, Throwable cause) {        super(message, cause);    }    public BaseException setExt(Serializable obj) {        return this;    }    public String toJson() {        return "";    }    public boolean isAjax() {        return isAjax;    }    public void setAjax(boolean isAjax) {        this.isAjax = isAjax;    }    public BaseException setAjax() {        this.isAjax = true;        return this;    }}